import {FETCH_CURRENCY} from './types';

apiToken= 'BwzDKO8Cn6yrzi4PrZLUYn94Ily8LQpdsIWUA9stW2UHBJBxA1IAyGek4Lil';
baseURI = 'https://api.worldtradingdata.com/api/v1/';
proxyUrl = 'https://cors-anywhere.herokuapp.com/';
baseCurrency = "USD";

export const fetchCurrencyRate=()=>dispatch=>{
    fetch(this.proxyUrl+this.baseURI+'forex?base='+this.baseCurrency+'&api_token='+this.apiToken)
        .then(res=>res.json()).then(post=>dispatch({
            type: FETCH_CURRENCIES,
            payload: posts
          }));
};
