import {FETCH_CURRENCY} from './actions/types';

const initialState = {
    currency: [],
    baseCurrency: {}
  };

  export default function(state = initialState, action) {
    switch (action.type) {
      case FETCH_CURRENCIES:
        return {
          ...state,
          items: action.payload
        };
      
      default:
        return state;
    }
  }
